import { useEffect, useState } from 'react';

import { useDebounce } from './hooks';
// import SearchBar from './SearchBar';
// import { fetchUsers, User } from './utils';
import axios from 'axios';
import Box from '@mui/material/Box';
import InputLabel from '@mui/material/InputLabel';
import MenuItem from '@mui/material/MenuItem';
import FormControl from '@mui/material/FormControl';
import Select from '@mui/material/Select';
import TextField from '@mui/material/TextField';
import Grid from '@mui/material/Grid';
import Paper from '@mui/material/Paper';
import { styled } from '@mui/material/styles';

const Item = styled(Paper)(({ theme }) => ({
  backgroundColor: theme.palette.mode === 'dark' ? '#1A2027' : '#fff',
  ...theme.typography.body2,
  padding: theme.spacing(1),
  textAlign: 'center',
  color: theme.palette.text.secondary,
}));

export default function Demo() {
  const [loading, setLoading] = useState(false);
  const [search, setSearch] = useState('');
  const debouncedSearch = useDebounce(search);
  const [users, setUsers] = useState<any[]>([]);
  const [searchBy, setSearchBy] = useState('');

  const searchByOptions = [
    { value: 'university', label: 'Search By University Name' },
    { value: 'country', label: 'Search By Country' },
  ]

  useEffect(() => {
    const loadUsers = async () => {
      setLoading(true);
      // if (debouncedSearch !== '' && debouncedSearch.length > 3) {
      //   await axios.post('/search-term', {
      //     name: debouncedSearch
      //   });
      // }
      let termTextCountry = '';
      let termTextUniversity = '';
      if (searchBy === 'university') {
        termTextUniversity = debouncedSearch
      } else if (searchBy === 'country') {
        termTextCountry = debouncedSearch;
      }
      const response: any = await axios.get(`http://universities.hipolabs.com/search?name=${termTextUniversity}&country=${termTextCountry}`);
      if (response.status === 200) {
        setUsers(response.data);
      }

      setLoading(false);
    };
    loadUsers();
  }, [debouncedSearch, searchBy]);

  return (
    <div className='' style={{ marginTop: "50px", marginLeft: "20px" }}>
      <Box sx={{ flexGrow: 1 }}>
        <Grid container spacing={2}>
          <Grid item xs={4} md={4}>
            <Item>
              <Box
                component="form"
                sx={{
                  '& > :not(style)': { width: '100%' },
                }}

              >
                <FormControl >
                  <InputLabel id="searchBy">Search By</InputLabel>
                  <Select
                    labelId="searchBy"
                    id="searchBy"
                    value={searchBy}
                    label="searchBy"
                    onChange={(e: any) => setSearchBy(e.target.value)}
                  >
                    <MenuItem value='' />
                    {searchByOptions.map(item => (
                      <MenuItem value={item.value} key={item.value}>{item.label}</MenuItem>
                    ))}
                  </Select>


                </FormControl>
              </Box>
            </Item>
          </Grid>
          <Grid item xs={4} md={4}>
            <Item>
              <Box
                component="form"
                sx={{
                  '& > :not(style)': { width: '100%' },
                }}
                noValidate
                autoComplete="off"
              >
                <TextField id="outlined-basic" label="Search" variant="outlined" onChange={(e: any) => setSearch(e.target.value)} placeholder='Search users' />
              </Box>
            </Item>
          </Grid>
        </Grid>
      </Box>

      {loading && <div>Loading...</div>}
      {!loading &&
        users.length > 0 && users.map((user) => {
          return <div key={user.id}>{user.name}</div>;
        })}
    </div>
  );
}
