import { useEffect, useState } from 'react';

import { useDebounce } from './hooks';
import SearchBar from './SearchBar';
import { fetchUsers, User } from './utils';
import axios from 'axios';
import Box from '@mui/material/Box';
import InputLabel from '@mui/material/InputLabel';
import MenuItem from '@mui/material/MenuItem';
import FormControl from '@mui/material/FormControl';
import Select, { SelectChangeEvent } from '@mui/material/Select';
import TextField from '@mui/material/TextField';


export default function Demo() {
  const [loading, setLoading] = useState(false);
  const [search, setSearch] = useState('');
  const debouncedSearch = useDebounce(search);
  const [users, setUsers] = useState<any[]>([]);
  const [searchBy, setSearchBy] = useState('');

  const handleChange = (event: SelectChangeEvent) => {
    console.log('event.target.value', event.target.value)
    setSearchBy(event.target.value);
  };
  useEffect(() => {
    console.log('setSearchBy', setSearchBy)
    const loadUsers = async () => {
      setLoading(true);
      console.log('debouncedSearch', debouncedSearch)
      const response: any = await axios.get(`http://universities.hipolabs.com/search?name=${debouncedSearch}?`);
      if (response.status == 200) {
        setUsers(response.data);
      }

      setLoading(false);
    };
    loadUsers();
  }, [debouncedSearch]);

  return (
    <div className='tutorial' style={{ marginTop: "100px" }}>
      <Box>
        <FormControl fullWidth>
          <InputLabel id="searchBy">Search By</InputLabel>
          <Select
            labelId="searchBy"
            id="searchBy"
            value={searchBy}
            label="searchBy"
          //onChange={handleChange}
           onChange={(e) => setSearchBy(e.target.value)}
          >
            <MenuItem value={1}>Search By University Name</MenuItem>
            <MenuItem value={2}>Search By Country</MenuItem>
          </Select>

          <Box
            component="form"
            sx={{
              '& > :not(style)': { m: 1, width: '25ch' },
            }}
            noValidate
            autoComplete="off"
          >
            <TextField id="outlined-basic" label="Outlined" variant="outlined" onChange={(e) => setSearch(e.target.value)} placeholder='Search users' />
          </Box>
        </FormControl>
      </Box>
      {loading && <div>Loading...</div>}
      {!loading &&
        users.length > 0 && users.map((user) => {
          return <div key={user.id}>{user.name}</div>;
        })}
    </div>
  );
}
