import { useEffect, useState } from 'react';

import { useDebounce } from './hooks';
import SearchBar from './SearchBar';
import { fetchUsers, User } from './utils';
import axios from 'axios';
import Box from '@mui/material/Box';
import InputLabel from '@mui/material/InputLabel';
import MenuItem from '@mui/material/MenuItem';
import FormControl from '@mui/material/FormControl';
// import Select from '@mui/material/Select';
import TextField from '@mui/material/TextField';


export default function Demo() {
  const [loading, setLoading] = useState(false);
  const [search, setSearch] = useState('');
  const debouncedSearch = useDebounce(search);
  const [users, setUsers] = useState<any[]>([]);
  const [searchBy, setSearchBy] = useState('');

  useEffect(() => {
    console.log('setSearchBy', setSearchBy)
    const loadUsers = async () => {
      setLoading(true);
      console.log('debouncedSearch', debouncedSearch)
      const response: any = await axios.get(`http://universities.hipolabs.com/search?name=${debouncedSearch}?`);
      if (response.status === 200) {
        setUsers(response.data);
      }

      setLoading(false);
    };
    loadUsers();
  }, [debouncedSearch, searchBy]);

  return (
    <div className='tutorial' style={{ marginTop: "50px", marginLeft: "20px" }}>
      <Box>
        <FormControl >
          <InputLabel id="searchBy">Search By</InputLabel>
          <select
            //label="searchBy"
            id="searchBy"
            value={searchBy}
            //onChange={handleChange}
            //onChange={(e) => console.log(e.target.value)}
            onChange={(e) => setSearchBy(e.target.value as string)}
          >
            <option value={'university'}>Search By University Name</option>
            <option value={'country'}>Search By Country</option>
          </select>

          <Box
            component="form"
            sx={{
              '& > :not(style)': { m: 1, width: '25ch' },
            }}
            noValidate
            autoComplete="off"
          >
            <TextField id="outlined-basic" label="Search" variant="outlined" onChange={(e) => setSearch(e.target.value)} placeholder='Search users' />
          </Box>
        </FormControl>
      </Box>
      {loading && <div>Loading...</div>}
      {!loading &&
        users.length > 0 && users.map((user) => {
          return <div key={user.id}>{user.name}</div>;
        })}
    </div>
  );
}
